prompt --application/shared_components/logic/application_computations/open_tasks
begin
wwv_flow_api.create_flow_computation(
 p_id=>wwv_flow_api.id(705653916478797523)
,p_computation_sequence=>10
,p_computation_item=>'OPEN_TASKS'
,p_computation_point=>'BEFORE_HEADER'
,p_computation_type=>'QUERY'
,p_computation_processed=>'REPLACE_EXISTING'
,p_computation=>wwv_flow_string.join(wwv_flow_t_varchar2(
'select sum(count_tasks) as open_tasks from (',
'select count(table_id) as count_tasks',
'from dgm_bg_tables ',
'where (description_de is null and description_en is null)',
'and owner_id = (select distinct owner_id from DWHGDST.dgm_bg_owner_pers where upper(pers_id) = upper(:APP_USER))',
'',
'union all',
'',
'select count(field_id) as count_tasks',
'from dgm_bg_fields',
'where (',
'       (SHORT_DESCRIPTION_DE is null and SHORT_DESCRIPTION_EN is null) or ',
'       (LONG_DESCRIPTION_DE is null and LONG_DESCRIPTION_EN is null)',
'      )',
'and owner_id = (select distinct owner_id from DWHGDST.dgm_bg_owner_pers where upper(pers_id) = upper(:APP_USER))',
'',
'union all',
'',
'select count(value_id) as count_tasks',
'from dgm_bg_values',
'where (description_de is null and description_en is null)',
'and field_id in (select distinct field_id from dgm_bg_fields where flag_check_values in (''YY'',''Y''))',
'and field_id in (select distinct field_id from dgm_bg_fields where owner_id = (select distinct owner_id from DWHGDST.dgm_bg_owner_pers where upper(pers_id) = upper(:APP_USER)))',
')'))
);
end;
