,p_plug_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'   select tab.dwh_scheme,',
'          tab.table_name as table_name,',
'          fd.field_name as field_name,',
'          case when fd.data_lengths is null',
'               then fd.data_types',
'               else fd.data_types||'' (''||fd.data_lengths||'')''',
'               end as data_type,',
'          nvl((select ower_name from dgm_bg_data_owner where owner_id = fd.owner_id),''No Data Owner'') as data_owner,',
'          case when (fd.SHORT_DESCRIPTION_DE is null and fd.SHORT_DESCRIPTION_EN is null and fd.LONG_DESCRIPTION_DE is null and fd.LONG_DESCRIPTION_EN is null)',
'               then ''Not available ''||''<span class="fa fa-bell-o fam-blank fam-is-danger"></span>''',
'               when ((fd.SHORT_DESCRIPTION_DE is null and fd.SHORT_DESCRIPTION_EN is null) or (fd.LONG_DESCRIPTION_DE is null and fd.LONG_DESCRIPTION_EN is null))',
'               then ''Partly available ''||''<span class="fa fa-bell-o fam-blank fam-is-warning"></span>''',
'               else ''Available ''||''<span class="fa fa-bell-o fam-blank fam-is-success"></span>'' end as description_available,',
'           case when fd.FLAG_CHECK_VALUES IN (''N'', ''NN'') ',
'                then ''Values not displayed ''||''<span class="fa fa-bell-o fam-blank fam-is-warning"></span>''',
'                when (fd.FLAG_CHECK_VALUES IN (''Y'', ''YY'') and nvl(anz_values.cnt_values,0) > 0 and nvl(anz_values.missing_desc,0) = 0)',
'                then ''All values described ''||''<span class="fa fa-bell-o fam-blank fam-is-success"></span>''',
'                when (fd.FLAG_CHECK_VALUES IN (''Y'', ''YY'') and nvl(anz_values.cnt_values,0) = 0 and nvl(anz_values.missing_desc,0) = 0)',
'                then ''No values available ''||''<span class="fa fa-bell-o fam-blank fam-is-warning"></span>''',
'                when (fd.FLAG_CHECK_VALUES IN (''Y'', ''YY'') and nvl(anz_values.cnt_values,0) > 0 and nvl(anz_values.missing_desc,0) = nvl(anz_values.cnt_values,0))',
'                then ''No values described ''||''<span class="fa fa-bell-o fam-blank fam-is-danger"></span>''',
'                else ''No values available ''||''<span class="fa fa-bell-o fam-blank fam-is-warning"></span>''',
'                end as value_desc_available,',
'           tab.category_id as id_category,     ',
'           fd.owner_id as id_owner,',
'           tab.table_id,',
'           fd.field_id',
'     from DGM_BG_TABLES tab',
'left join DGM_BG_FIELDS fd',
'       on tab.table_id = fd.table_id',
'left join DGM_BG_VALUES val',
'       on fd.field_id = val.field_id',
'left join ',
'            ( ',
'              SELECT field_id,',
'                     count(*) cnt_values,',
'                     sum(case when description_de is null and description_en is null',
'                              then 1 else 0 end) missing_desc',
'                FROM dgm_bg_values',
'            GROUP BY field_id',
'            ) anz_values',
'        on fd.field_id = anz_values.field_id ',
'        ',
'where (',
'       UPPER(tab.table_name) LIKE UPPER(''%''||:P10_GLOBAL_DEF||''%'') or',
'       UPPER(tab.description_de) LIKE UPPER(''%''||:P10_GLOBAL_DEF||''%'') or ',
'       UPPER(tab.description_en) LIKE UPPER(''%''||:P10_GLOBAL_DEF||''%'') or',
'       UPPER(fd.field_name) LIKE UPPER(''%''||:P10_GLOBAL_DEF||''%'') or',
'       UPPER(fd.short_description_en) LIKE UPPER(''%''||:P10_GLOBAL_DEF||''%'') or',
'       UPPER(fd.short_description_de) LIKE UPPER(''%''||:P10_GLOBAL_DEF||''%'') or',
'       UPPER(fd.long_description_en) LIKE UPPER(''%''||:P10_GLOBAL_DEF||''%'') or',
'       UPPER(fd.long_description_de) LIKE UPPER(''%''||:P10_GLOBAL_DEF||''%'')',
'      )     ',
'  ',
'  and nvl((select ower_name from dgm_bg_data_owner where owner_id = fd.owner_id),''No Data Owner'')',
'       in (select regexp_substr(nvl(:P10_OWNER_GLOBAL, :P10_DATA_OWNER_ALL),''[^:]+'', 1, level) from dual',
'                      connect by regexp_substr(nvl(:P10_OWNER_GLOBAL, :P10_DATA_OWNER_ALL), ''[^:]+'', 1, level) is not null)',
'',
'  and nvl(fd.data_types,''No Data-Type'')',
'       in (select regexp_substr(nvl(:P10_DATATYPES_GLOBAL, :P10_DATA_TYPES_ALL),''[^:]+'', 1, level) from dual',
'                      connect by regexp_substr(nvl(:P10_DATATYPES_GLOBAL, :P10_DATA_TYPES_ALL), ''[^:]+'', 1, level) is not null)',
'                      '))
