'select * from (',
'select  tabs.DWH_SCHEME,',
'        tabs.TABLE_ID,',
'        tabs.TABLE_NAME,',
'        (select category_name from DGM_BG_CATEGORIES where category_id = tabs.category_id) as CATEGORY_ID,',
'        tabs.DESCRIPTION_DE,',
'        tabs.DESCRIPTION_EN,',
'        (select first_name||'' ''||last_name from DGM_ADM_PERSONAL where upper(persnr) = upper(tabs.MODIFIED_BY)) MODIFIED_BY,',
